mapper.update:
update orders
set state = #{state,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT sum(t.count) as sum from (
SELECT t.count from ( SELECT scg.goods_id AS goodId, scg.count FROM ( SELECT id AS cartId FROM shopping_cart WHERE buyer_id = #{buyerId} ) sc LEFT JOIN (select * from shopping_cart_goods where state=1) scg ON sc.cartId = scg.shopping_cart_id ) t where t.goodId=#{goodId}
UNION SELECT count(1) as count FROM ( SELECT o.*, og.id AS og_id, og.good_id FROM ( SELECT id FROM orders WHERE buyer_id = #{buyerId} AND state != 7 ) o LEFT JOIN order_good og ON o.id = og.order_id ) t WHERE t.good_id = #{goodId} ) t
